import React from 'react';
import {Button} from "@nextui-org/react";

interface Props {
    children: React.ReactNode;
    color?: "primary" | "transparent" | "danger";
    className?: string;
}

function MyButton({children, className}: Props) {
    let color = ""
    if (color === "primary") {
        color = `text-white bg-[#1560BD] hover:bg-[#0f488e]`
    }else if (color === "transparent") {
        color = `text black bg-transparent border border-transparent hover:border-[#1560BD] hover:text-[#1560BD]`
    }else if (color === "danger") {
        color = `bg-[#1560BD] hover:bg-[#0f488e]`
    }
    return (
        <Button className={`${className} rounded  transition ${color}`}>
            {children}
        </Button>
    );
}

export default MyButton;
